<script>
    import { onMount } from "svelte";
    import { navigate, showModal } from "svelte-native";
    import TwitchOauth from "@/components/TwitchOauth.svelte";
    import { setUser } from "~/lib/stores";
    
    let isMounted = false
    let isFetching = false
    
    onMount(() => {
        isMounted = true
    })

    async function triggerOauth(){
        let data = await showModal({ page: TwitchOauth, fullscreen: false })
        setUser(data)
    }
</script>

<page actionBarHidden="true" class="bg-primary">
  <flexboxLayout horizontalAlignment="center" verticalAlignment="center" class="flex-col" on:tap={triggerOauth}>
      {#if isFetching}
          Loading
      {:else}
          <!-- Tower logo (no text) -->
          <image 
              src="data:image/png;base64," 
              width="96" height="96" 
              stretch="aspectFit"
          />
          <flexboxLayout class="items-center mt-10 text-center text-white rounded bg-[#5F3AAD] shadow-dreamy pointer-cursor">
              <flexboxLayout class="justify-center items-center w-12 h-12 bg-[#56349C] p-3 rounded-l">
                  <image 
                      src="data:image/png;base64,"
                      stretch="aspectFit"
                  />
              </flexboxLayout>
              <label text="Continue with Twitch.tv" class="px-8" />
          </flexboxLayout>
      {/if}
  </flexboxLayout>
</page>